mapper.select:
select 

from x_items_batch_tbl
where ACTIVITY_UID = #{activityUid,jdbcType=VARCHAR}
  and WH_CODE = #{whCode,jdbcType=VARCHAR}
  and ITEM_CODE = #{itemCode,jdbcType=VARCHAR}
  and COMBINATION1_CODE = #{combination1Code,jdbcType=VARCHAR}
  and COMBINATION2_CODE = #{combination2Code,jdbcType=VARCHAR}
  and BATCH_NUMBER_S4 = #{batchNumberS4,jdbcType=VARCHAR}

mapper.delete:
delete from x_items_batch_tbl
where ACTIVITY_UID = #{activityUid,jdbcType=VARCHAR}
  and WH_CODE = #{whCode,jdbcType=VARCHAR}
  and ITEM_CODE = #{itemCode,jdbcType=VARCHAR}
  and COMBINATION1_CODE = #{combination1Code,jdbcType=VARCHAR}
  and COMBINATION2_CODE = #{combination2Code,jdbcType=VARCHAR}
  and BATCH_NUMBER_S4 = #{batchNumberS4,jdbcType=VARCHAR}

mapper.update:
update x_items_batch_tbl


    BATCH = #{batch,jdbcType=VARCHAR},


    EXPIRATION_DATE = #{expirationDate,jdbcType=TIMESTAMP},


    STOCK = #{stock,jdbcType=DECIMAL},


    VERSION = #{version,jdbcType=BIGINT},


where ACTIVITY_UID = #{activityUid,jdbcType=VARCHAR}
  and WH_CODE = #{whCode,jdbcType=VARCHAR}
  and ITEM_CODE = #{itemCode,jdbcType=VARCHAR}
  and COMBINATION1_CODE = #{combination1Code,jdbcType=VARCHAR}
  and COMBINATION2_CODE = #{combination2Code,jdbcType=VARCHAR}
  and BATCH_NUMBER_S4 = #{batchNumberS4,jdbcType=VARCHAR}

update x_items_batch_tbl
set BATCH = #{batch,jdbcType=VARCHAR},
  EXPIRATION_DATE = #{expirationDate,jdbcType=TIMESTAMP},
  STOCK = #{stock,jdbcType=DECIMAL},
  VERSION = #{version,jdbcType=BIGINT}
where ACTIVITY_UID = #{activityUid,jdbcType=VARCHAR}
  and WH_CODE = #{whCode,jdbcType=VARCHAR}
  and ITEM_CODE = #{itemCode,jdbcType=VARCHAR}
  and COMBINATION1_CODE = #{combination1Code,jdbcType=VARCHAR}
  and COMBINATION2_CODE = #{combination2Code,jdbcType=VARCHAR}
  and BATCH_NUMBER_S4 = #{batchNumberS4,jdbcType=VARCHAR}
