mapper.select:
select 

from x_local_fidelizados_tbl
where UID_INSTANCIA = #{uidInstancia,jdbcType=VARCHAR}
  and ID_FIDELIZADO = #{idFidelizado,jdbcType=BIGINT}

mapper.delete:
delete from x_local_fidelizados_tbl
where UID_INSTANCIA = #{uidInstancia,jdbcType=VARCHAR}
  and ID_FIDELIZADO = #{idFidelizado,jdbcType=BIGINT}
