mapper.select:
select 

from x_proformas_lineas_tbl
where UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and ID_PROFORMA = #{idProforma,jdbcType=VARCHAR}
  and LINEA = #{linea,jdbcType=INTEGER}

mapper.delete:
delete from x_proformas_lineas_tbl
where UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and ID_PROFORMA = #{idProforma,jdbcType=VARCHAR}
  and LINEA = #{linea,jdbcType=INTEGER}

mapper.update:
update x_proformas_lineas_tbl
set CODART = #{codart,jdbcType=VARCHAR},
  UNIDADES = #{unidades,jdbcType=INTEGER},
  CANTIDAD_CONVERTIDA = #{cantidadConvertida,jdbcType=DECIMAL},
  CANTIDAD_SUMINISTRADA = #{cantidadSuministrada,jdbcType=DECIMAL},
  UNIDAD_MEDIDA_SUMINISTRADA = #{unidadMedidaSuministrada,jdbcType=VARCHAR},
  DESCUENTO = #{descuento,jdbcType=VARCHAR},
  IMPORTE = #{importe,jdbcType=DECIMAL},
  LOTE = #{lote,jdbcType=VARCHAR},
  FECHA_CADUCIDAD = #{fechaCaducidad,jdbcType=TIMESTAMP}
where UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and ID_PROFORMA = #{idProforma,jdbcType=VARCHAR}
  and LINEA = #{linea,jdbcType=INTEGER}
