mapper.select:
SELECT PAI.UID_INSTANCIA, CONCAT ('(', PAI.CODPAIS, ')', PAI.DESPAIS) AS CODPAIS , PRE.PREFIJO 
FROM 
	X_PAISES_PREFIJOS_TBL PRE INNER JOIN
	D_PAISES_TBL PAI ON (PRE.UID_INSTANCIA = PAI.UID_INSTANCIA AND PRE.CODPAIS = PAI.CODPAIS)
WHERE PAI.UID_INSTANCIA = #{uidInstancia,jdbcType=VARCHAR}
AND PAI.CODPAIS = #{codPais,jdbcType=VARCHAR}
