mapper.select:
select 

from x_detail_pets_tbl
where ACTIVITY_ID = #{activityId,jdbcType=VARCHAR}
  and TIPO_MATERIAL = #{tipoMaterial,jdbcType=VARCHAR}
  and GRUPO_ARTICULOS = #{grupoArticulos,jdbcType=VARCHAR}
  and VALOR_CARACTERISTICA = #{valorCaracteristica,jdbcType=VARCHAR}
  and PAIS = #{pais,jdbcType=VARCHAR}
  and REGION = #{region,jdbcType=VARCHAR}

mapper.delete:
delete from x_detail_pets_tbl
where ACTIVITY_ID = #{activityId,jdbcType=VARCHAR}
  and TIPO_MATERIAL = #{tipoMaterial,jdbcType=VARCHAR}
  and GRUPO_ARTICULOS = #{grupoArticulos,jdbcType=VARCHAR}
  and VALOR_CARACTERISTICA = #{valorCaracteristica,jdbcType=VARCHAR}
  and PAIS = #{pais,jdbcType=VARCHAR}
  and REGION = #{region,jdbcType=VARCHAR}

mapper.update:
update x_detail_pets_tbl


    JERARQUIA = #{jerarquia,jdbcType=VARCHAR},


    ID_CHIP = #{idChip,jdbcType=CHAR},


    ID_ANILLA = #{idAnilla,jdbcType=CHAR},


    ID_CITES = #{idCites,jdbcType=CHAR},


    CONTRATO = #{contrato,jdbcType=CHAR},


where ACTIVITY_ID = #{activityId,jdbcType=VARCHAR}
  and TIPO_MATERIAL = #{tipoMaterial,jdbcType=VARCHAR}
  and GRUPO_ARTICULOS = #{grupoArticulos,jdbcType=VARCHAR}
  and VALOR_CARACTERISTICA = #{valorCaracteristica,jdbcType=VARCHAR}
  and PAIS = #{pais,jdbcType=VARCHAR}
  and REGION = #{region,jdbcType=VARCHAR}

update x_detail_pets_tbl
set JERARQUIA = #{jerarquia,jdbcType=VARCHAR},
  ID_CHIP = #{idChip,jdbcType=CHAR},
  ID_ANILLA = #{idAnilla,jdbcType=CHAR},
  ID_CITES = #{idCites,jdbcType=CHAR},
  CONTRATO = #{contrato,jdbcType=CHAR}
where ACTIVITY_ID = #{activityId,jdbcType=VARCHAR}
  and TIPO_MATERIAL = #{tipoMaterial,jdbcType=VARCHAR}
  and GRUPO_ARTICULOS = #{grupoArticulos,jdbcType=VARCHAR}
  and VALOR_CARACTERISTICA = #{valorCaracteristica,jdbcType=VARCHAR}
  and PAIS = #{pais,jdbcType=VARCHAR}
  and REGION = #{region,jdbcType=VARCHAR}
