  and CODALM = #{codalm,jdbcType=VARCHAR}
  and CODART = #{codart,jdbcType=VARCHAR}
  and DESGLOSE1 = #{desglose1,jdbcType=VARCHAR}
  and DESGLOSE2 = #{desglose2,jdbcType=VARCHAR}
  and LINEA = #{linea,jdbcType=INTEGER}

mapper.update:
