mapper.select:
select 

from F_FIDELIZADOS_DIR
where UID_INSTANCIA = #{uidInstancia,jdbcType=VARCHAR}
  and ID_FIDELIZADO = #{idFidelizado,jdbcType=DECIMAL}
  and DESCRIPCION_DIRECCION = #{descripcionDireccion,jdbcType=VARCHAR}

mapper.delete:
delete from F_FIDELIZADOS_DIR_TBL
where UID_INSTANCIA = #{uidInstancia,jdbcType=VARCHAR}
  and ID_FIDELIZADO = #{idFidelizado,jdbcType=DECIMAL}
  and DESCRIPCION_DIRECCION = #{descripcionDireccion,jdbcType=VARCHAR}

mapper.insert:
insert into F_FIDELIZADOS_DIR_TBL (UID_INSTANCIA, ID_FIDELIZADO, DESCRIPCION_DIRECCION, 
  DOMICILIO, POBLACION, LOCALIDAD, 
  PROVINCIA, CP, CODPAIS
  )
values (#{uidInstancia,jdbcType=VARCHAR}, #{idFidelizado,jdbcType=DECIMAL}, #{descripcionDireccion,jdbcType=VARCHAR}, 
  #{domicilio,jdbcType=VARCHAR}, #{poblacion,jdbcType=VARCHAR}, #{localidad,jdbcType=VARCHAR}, 
  #{provincia,jdbcType=VARCHAR}, #{cp,jdbcType=VARCHAR}, #{codPais,jdbcType=VARCHAR}
  )

mapper.update:
update F_FIDELIZADOS_DIR_TBL


    DOMICILIO = #{domicilio,jdbcType=VARCHAR},


    POBLACION = #{poblacion,jdbcType=VARCHAR},


    LOCALIDAD = #{localidad,jdbcType=VARCHAR},


    PROVINCIA = #{provincia,jdbcType=VARCHAR},


    CP = #{cp,jdbcType=VARCHAR},


    CODPAIS = #{codPais,jdbcType=VARCHAR},


where UID_INSTANCIA = #{uidInstancia,jdbcType=VARCHAR}
  and ID_FIDELIZADO = #{idFidelizado,jdbcType=DECIMAL}
  and DESCRIPCION_DIRECCION = #{descripcionDireccion,jdbcType=VARCHAR}

update F_FIDELIZADOS_DIR_TBL
set DOMICILIO = #{domicilio,jdbcType=VARCHAR},
  POBLACION = #{poblacion,jdbcType=VARCHAR},
  LOCALIDAD = #{localidad,jdbcType=VARCHAR},
  PROVINCIA = #{provincia,jdbcType=VARCHAR},
  CP = #{cp,jdbcType=VARCHAR},
  CODPAIS = #{codPais,jdbcType=VARCHAR}
where UID_INSTANCIA = #{uidInstancia,jdbcType=VARCHAR}
  and ID_FIDELIZADO = #{idFidelizado,jdbcType=DECIMAL}
  and DESCRIPCION_DIRECCION = #{descripcionDireccion,jdbcType=VARCHAR}

update F_FIDELIZADOS_DIR_TBL

  DESCRIPCION_DIRECCION = #{descripcionDireccionNew,jdbcType=VARCHAR},

where UID_INSTANCIA = #{uidInstancia,jdbcType=VARCHAR}
  and ID_FIDELIZADO = #{idFidelizado,jdbcType=DECIMAL}
  and DESCRIPCION_DIRECCION = #{descripcionDireccionOld,jdbcType=VARCHAR}
