mapper.select:
select
distinct
from TV_LISTAS_DESEOS_DET_TBL LISTAS INNER JOIN D_ARTICULOS_TBL ARTICULOS on (ARTICULOS.UID_ACTIVIDAD = LISTAS.UID_ACTIVIDAD AND ARTICULOS.CODART = LISTAS.CODART)
order by ${orderByClause}

select 

from TV_LISTAS_DESEOS_DET_TBL LISTAS
	INNER JOIN D_ARTICULOS_TBL ARTICULOS on (ARTICULOS.UID_ACTIVIDAD = LISTAS.UID_ACTIVIDAD AND ARTICULOS.CODART = LISTAS.CODART)
where UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and UID_LISTA_DESEOS = #{uidListaDeseos,jdbcType=VARCHAR}
  and LINEA = #{linea,jdbcType=DECIMAL}
  and CODART = #{codart,jdbcType=VARCHAR}
  and DESGLOSE1 = #{desglose1,jdbcType=VARCHAR}
  and DESGLOSE2 = #{desglose2,jdbcType=VARCHAR}

mapper.delete:
delete from TV_LISTAS_DESEOS_DET_TBL
where UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and UID_LISTA_DESEOS = #{uidListaDeseos,jdbcType=VARCHAR}
  and LINEA = #{linea,jdbcType=DECIMAL}
  and CODART = #{codart,jdbcType=VARCHAR}
  and DESGLOSE1 = #{desglose1,jdbcType=VARCHAR}
  and DESGLOSE2 = #{desglose2,jdbcType=VARCHAR}

mapper.insert:
insert into TV_LISTAS_DESEOS_DET_TBL (UID_ACTIVIDAD, UID_LISTA_DESEOS, LINEA, 
  CODART, DESGLOSE1, DESGLOSE2, 
  CANTIDAD, UNIDAD_MEDIDA, CANTIDAD_MEDIDA
  )
values (#{uidActividad,jdbcType=VARCHAR}, #{uidListaDeseos,jdbcType=VARCHAR}, #{linea,jdbcType=DECIMAL}, 
  #{codart,jdbcType=VARCHAR}, #{desglose1,jdbcType=VARCHAR}, #{desglose2,jdbcType=VARCHAR}, 
  #{cantidad,jdbcType=DECIMAL}, #{unidadMedida,jdbcType=VARCHAR}, #{cantidadMedida,jdbcType=DECIMAL}
  )

mapper.update:
update TV_LISTAS_DESEOS_DET_TBL


    CANTIDAD = #{cantidad,jdbcType=DECIMAL},


    UNIDAD_MEDIDA = #{unidadMedida,jdbcType=VARCHAR},


    CANTIDAD_MEDIDA = #{cantidadMedida,jdbcType=DECIMAL},


where UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and UID_LISTA_DESEOS = #{uidListaDeseos,jdbcType=VARCHAR}
  and LINEA = #{linea,jdbcType=DECIMAL}
  and CODART = #{codart,jdbcType=VARCHAR}
  and DESGLOSE1 = #{desglose1,jdbcType=VARCHAR}
  and DESGLOSE2 = #{desglose2,jdbcType=VARCHAR}

update TV_LISTAS_DESEOS_DET_TBL
set CANTIDAD = #{cantidad,jdbcType=DECIMAL},
  UNIDAD_MEDIDA = #{unidadMedida,jdbcType=VARCHAR},
  CANTIDAD_MEDIDA = #{cantidadMedida,jdbcType=DECIMAL}
where UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and UID_LISTA_DESEOS = #{uidListaDeseos,jdbcType=VARCHAR}
  and LINEA = #{linea,jdbcType=DECIMAL}
  and CODART = #{codart,jdbcType=VARCHAR}
  and DESGLOSE1 = #{desglose1,jdbcType=VARCHAR}
  and DESGLOSE2 = #{desglose2,jdbcType=VARCHAR}

