mapper.select:
select 

from LY_TRIGGERS_EXEC_TBL TEXEC
where TEXEC.UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and TEXEC.TRIGGER_EXEC_UID = #{triggerExecUid,jdbcType=VARCHAR}

mapper.delete:
delete from LY_TRIGGERS_EXEC_TBL
where UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and TRIGGER_EXEC_UID = #{triggerExecUid,jdbcType=VARCHAR}

mapper.select:
    select 

    ,

FROM LY_TRIGGERS_EXEC_TBL TEXEC
   LEFT JOIN LY_ACTIONS_EXEC_TBL AEXEC ON (AEXEC.UID_ACTIVIDAD = TEXEC.UID_ACTIVIDAD AND AEXEC.ACC_EXECUTION_UID = TEXEC.LAST_ACC_EXECUTION_UID)
    where TEXEC.UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
      and TEXEC.TRIGGER_EXEC_UID = #{triggerExecUid,jdbcType=VARCHAR}
